import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { withRouter} from 'react-router-dom';

export class Footer extends Component{
    constructor(){
        super()
        this.state = {
            formattedStocks: []
        }
    }

    componentDidMount(){
        let stocks = []
        let buffer = ""
        this.props.watchlists.watchlists[0].stocks.forEach(stock => {
            buffer = ""
            fetch('https://finnhub.io/api/v1/quote?symbol=' + stock + '&token=brain17rh5rbgnjpuck0')
                .then(res => res.json())
                .then(data =>{
                    buffer += stock + "| $" + data.c.toFixed(2) + " " + (data.pc - data.c).toFixed(2)
                })
            stocks.push(buffer)
        })
        console.log(stocks)
        this.setState({formattedStocks: stocks})
    }
    render(){
        return(
            <div id="watchlist_bar">
                {this.state.formattedStocks.map(stock => <div id="watchlist_item">{stock}</div>)}
            </div>  
        )
    }
}

Footer.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired,
    watchlists: PropTypes.object.isRequired
  };

  const mapStateToProps = state => ({
    auth: state.auth, 
    user: state.user,
    errors: state.errors,
    watchlists: state.watchlists
  });

  export default connect(mapStateToProps)(withRouter(Footer));
