import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../actions/authActions";

export class RegisterComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            name: "",
            email: "",
            password: "",
            password2: "",
            errors: {}
          };
    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
          this.setState({
            errors: nextProps.errors
          });
        }
    }



    render(){
        return(
            <div class="row" id="register-component">
               <div class="card" id="register-card">
                    <div class="card-body" id="register-card-body">
                       <h4>Register</h4> 
                       <hr id="break"/>
                       <div id="error_text">
                       {this.state.errors != null? <span id="register_failed"><i>Email already in use. Please log in with your email.</i></span>: null}
                       </div>
                       <form noValidate onSubmit={this.onSubmit}>
                        <div class="form-group">
                            <label id="email_label"for="email_input">Email address</label>
                            <input type="email" onChange={this.onUsernameChange} class="form-control" id="email_input" aria-describedby="emailHelp" placeholder="Enter email"/>
                        </div>
                        <div class="form-group">
                            <label id="password_label" for="password_input">Password</label>
                            <input type="password" onChange={this.onPasswordChange} class="form-control" id="password_input" placeholder="Password" />
                        </div> 
                        <button type="submit" class="btn btn-primary">Submit</button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}
RegisterComponent.propTypes = {
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { registerUser }
)(withRouter(RegisterComponent));
